function init_profile_form(){var i=$("#user_form");i.validate(),$("#profie-form-ajax-password").validate({submitHandler:function(){showLoading();var i=$(this.currentForm);return $.post(i.attr("action"),i.serialize(),function(a){i.flash_message(a)}).complete(function(){hideLoading()}),!1}}),i.find(".btn_change_photo").click(function(){return $.fn.upload_filemanager({formats:"image",selected:function(a){i.find("#user_meta_avatar").val(a.url),i.find("img.img-thumbnail").attr("src",a.url)}}),!1})}